import { useState, useEffect } from "react";
import axios from "axios";

function BookingForm() {
    const [movies, setMovies] = useState([]);
    const [selectedMovie, setSelectedMovie] = useState("");
    const [time, setTime] = useState("");
    const [name, setName] = useState("");

    useEffect(() => {
        axios.get("http://localhost:8000/movies").then((res) => setMovies(res.data));
    }, []);

    const handleBooking = () => {
        axios.post("http://localhost:8000/bookings", { name, movie: selectedMovie, time })
            .then((res) => alert(res.data.message));
    };

    return (
        <div className="p-4">
            <h2 className="text-xl font-bold">Book a Ticket</h2>
            <input className="border p-2 m-2 w-full" placeholder="Your Name" onChange={(e) => setName(e.target.value)} />
            <select className="border p-2 m-2 w-full" onChange={(e) => setSelectedMovie(e.target.value)}>
                <option>Select a Movie</option>
                {movies.map((movie) => (
                    <option key={movie.id} value={movie.name}>{movie.name}</option>
                ))}
            </select>
            <select className="border p-2 m-2 w-full" onChange={(e) => setTime(e.target.value)}>
                <option>Select a Time</option>
                {movies.find((m) => m.name === selectedMovie)?.timings.map((t) => (
                    <option key={t} value={t}>{t}</option>
                ))}
            </select>
            <button className="bg-blue-500 text-white p-2 m-2 w-full" onClick={handleBooking}>Confirm Booking</button>
        </div>
    );
}

export default BookingForm;
